<script>
import VueIcon from "../../../components/modules/icon/VueIcon.vue";
import VueTiptap from "../../../components/modules/VueTiptap.vue";
import VueButton from "../../../components/ui/VueButton.vue";

import InfoBar from "../../../components/ui/main/InfoBar.vue";
import Block from "../../../components/ui/main/Block.vue";
import Empty from "../../../components/modules/Empty.vue";
import LoadText from "../../../components/ui/main/LoadText.vue";

import api from "../../../mixin/api";
import sorted from "../../../services/sorted";
import fakeDelay from "../../../mixin/fake-delay";

export default {
	components: {
		VueIcon,
		VueTiptap,
		VueButton,

		InfoBar,
		Block,
		Empty,
		LoadText,
	},
	data() {
		return {
			loading: {
				loader: {
					vacancies: true,
				},
			},

			/* Данные */
			vacancies: [
				{
					id: 1,
					title: "",
					address: "",
					schedule: "",
					requirements: "",
					conditions: "",
					description: "",
					salary: 0,
				},
				{
					id: 2,
					title: "",
					address: "",
					schedule: "",
					requirements: "",
					conditions: "",
					description: "",
					salary: 0,
				},
				{
					id: 3,
					title: "",
					address: "",
					schedule: "",
					requirements: "",
					conditions: "",
					description: "",
					salary: 0,
				},
			],
		};
	},
	created() {
		fakeDelay(this.$store.getters.timeout, () =>
			api({
				method: "get",
				url: this.$store.getters.urlApi + `get-vacancies`,
			})
		).then((response) => {
			if (!response) return;

			try {
				for (let i = 0; i < response.data.result.length; i++) {
					this.vacancies[i] = response.data.result[i];
				}

				this.vacancies.splice(response.data.result.length, this.vacancies.length);

				this.loading.loader.vacancies = false;
			} catch (error) {
				this.$store.commit("addDebugger", {
					title: "Ошибка.",
					body: error,
					type: "error",
				});
			}
		});
	},
};
</script>

